import React, { Component } from 'react'
import LireComment from './LireComment';
import 'bootstrap/dist/css/bootstrap.css';
import './comment.css'
export default class Comment extends Component {
    constructor(props) {
      super(props);
      this.state = {
        term: '',
        items: [],
      };
    }
    onChange = (event) => {
      var checkErreurComment = document.getElementById('checkErreurComment')
      var val = checkErreurComment.value
      if(val ===""){
        console.log('pas OK')
      }else{
        this.setState({term: event.target.value});
        console.log('ok')
      }      
      }



    render() {
      return (
        <div>
            <form className="App" >
              <div id="margTop">
                <table>
                  <tbody>
                    <tr>
                    <td><input value={this.state.term} onChange={this.onChange} id="checkErreurComment"/></td>
                    <td><button onClick={(event) => {
                        event.preventDefault()
                      this.setState({
                        term: '',
                        items: [...this.state.items, this.state.term],

                      });

                    }} className = "btn btn-primary">Comment</button></td>
                  </tr>
                  </tbody>
                </table>
              </div>
            </form>
            <LireComment items={this.state.items}/>
        </div>
      );
    }
  }
