const initialState = {
  favoritesFilm: [
    {
      id: 1,
      image: "images/pic06.jpg",
      title: "Star Wars VIII - Les derniers Jedi",
      description: "Nouvel épisode de la saga. Les héros du Réveil de la force rejoignent les figures légendaires de la galaxie dans une aventure épique qui révèle des secrets ancestraux sur la Force et entraîne de choquantes révélations sur le passé…",
      favoris: false
    },
    {
      id: 2,
      image: "images/pic06.jpg",
      title: "La Guerre des étoiles",
      description: "Il y a bien longtemps, dans une galaxie très lointaine... La guerre civile fait rage entre l'Empire galactique et l'Alliance rebelle. Capturée par les troupes de choc de l'Empereur menées par le sombre et impitoyable Dark Vador, la princesse Leia Organa dissimule les plans de l’Étoile Noire, une station spatiale invulnérable, à son droïde R2-D2 avec pour mission de les remettre au Jedi Obi-Wan Kenobi. Accompagné de son fidèle compagnon, le droïde de protocole C-3PO, R2-D2 s'échoue sur la planète Tatooine et termine sa quête chez le jeune Luke Skywalker. Rêvant de devenir pilote mais confiné aux travaux de la ferme, ce dernier se lance à la recherche de ce mystérieux Obi-Wan Kenobi, devenu ermite au cœur des montagnes désertiques de Tatooine...",
      favoris: false
    }
  ]
}

function toggleFavorite(state = initialState, action) {
  let nextState
  switch (action.type) {
    case 'TOGGLE_FAVORITE':
    const favoriteFilmIndex = state.favoritesFilm.findIndex(item => item.id === action.value.id)
    for(let i = 0;i<state.favoritesFilm.length; i++){
      if(action.value == state.favoritesFilm[i].id && state.favoritesFilm[i].id==''){
        state.favoritesFilm[i].favoris = true
        console.log("etat favoris :"+state.favoritesFilm[i].favoris)
        nextState = {
          ...state,
          favoritesFilm: state.favoritesFilm.filter( (item, index) => index !== favoriteFilmIndex)
        }
      }
    }
    break;
    case 'TOGGLE_NONFAVORITE':
    for(let i = 0;i<state.favoritesFilm.length; i++){
      if(action.value == state.favoritesFilm[i].id){
        state.favoritesFilm[i].favoris =false
        console.log("etat nonfavoris :"+state.favoritesFilm[i].favoris)
      }
    }
    break;
    default:
      return state
  }
}

export default toggleFavorite
