import React from 'react'
import { connect } from 'react-redux'

class Design extends React.Component {

     mapStateToProps = (state) => {
        return{
            favoritesFilm: state.favoritesFilm
        }
      }
    _toggleFavorite(e) {
        const action = { type: "TOGGLE_FAVORITE",value:e }
        this.props.dispatch(action)
    }
    _toggleNonFavorite(e) {
        const action = { type: "TOGGLE_NONFAVORITE",value:e }
        this.props.dispatch(action)
    }
    componentDidMount(){
        console.log(" value favoris :"+this.props.favoritesFilm[0].favoris)
    }
    componentDidUpdate() {
        console.log("componentDidUpdate : ")
        console.log(this.props.favoritesFilm)
      }
    render() {
        console.log(this.props)

        return (

            <div className="container">
                <div className="row">
                    {
                        (this.props.favoritesFilm.length > 0) ? (this.props.favoritesFilm.map(items =>
                            <div className="col-md-4 col-sm-12 col-xs-12">
                                <div class="card cardTete">
                                    <img src={items.image} alt="yes" />
                                    <div class="card-body">
                                        <h5 class="card-title">{items.title}</h5>
                                        {
                                            (items.favoris)?(<center><span role="img" onClick={()=>this._toggleNonFavorite(items.id)}>ðŸ–¤</span></center>):(<center><span role="img" onClick={()=>this._toggleFavorite(items.id)}>â™¡</span></center>)}

                                        <p class="card-text">{items.description}</p>

                                    </div>
                                </div>
                            </div>)) : ('')
                    }


                </div>
            </div>
        )
    }
}
