import React, { useState } from 'react'
import 'bootstrap/dist/css/bootstrap.css';
import AffichageProd from './MesPackets/AffichageProd'
import AjouterProd from './MesPackets/AjouterProd'
import './App.css'

const App = () => {
  const ProduitsData = [
  ]

  const [Produits, setProduits] = useState(ProduitsData)
  const [count, setCount] = useState(0)

  const addProduit = produit => {
		produit.id = count
		setProduits([ ...Produits, produit ])
  }
  const somProd=()=>{
    var tot = document.getElementById('tot')
    let total=0;
    for(let i=0;i<Produits.length;i++){
        total=total+parseInt(Produits[i].prixProd);
    }
     let a=[total]

    var numberFormat = new Intl.NumberFormat("es-ES");
    var formatted = a.map(numberFormat.format);
    tot.innerHTML = formatted
    return formatted.join("; ")
    }
  return (
    <div>

    <div className="container">



          <AjouterProd addProduit={addProduit} setCount={setCount} count={count}/>
          <AffichageProd Produits={Produits}/>


    </div>
    <div className="container" >
    <div id="total">
    <button  onClick ={()=>{somProd()}} className="btn btn-warning withMarg">TOTAL</button>
    </div>
    <br/>
    <div id="divBoite"><p>TOTAL = &nbsp;&nbsp;<span id="tot"> </span>&nbsp;&nbsp;Ar</p></div>
    </div>
  </div>
  )

}

export default App
